import React, { useState } from 'react';
import './index.css';

function Card(props) {
    const [bg, setBg] = useState('#fff')
    const [color, setColor] = useState('#000')

    function bgColorChange() {
        setBg('#000')
        setColor('#fff')
    };

    function bgBack() {
        setBg('#fff')
        setColor('#000')
    };
    return (
        <>
            <div className="cards">
                <div className="card">
                    <img className="imgsrc" src={props.img_src} alt='my img' />
                    <div className="info">
                        <p style={{ backgroundColor: bg, color: color }} className='org_info'>{props.org_name}</p>
                        <h3 className='ser_name'>{props.ser_name}</h3>
                        <a href={props.button} target='_blank'>
                            <button>WATCH</button>
                        </a>
                    </div>
                </div>
            </div>
        </>
    );
};

export default Card;
