import React, { useState } from 'react';
import './index.css';
import Navbar from './Navbar';
import ToDoLiatsItems from './ToDoListsItems';
import App from './App';
// import '../node_modules/bootstrap/dist/css/bootstrap.min.css';

function ToDo_list() {
    const [items, setitens] = useState('');
    const [allItems, setallItems] = useState([]);

    function eventHandler(event) {
        setitens(event.target.value)
    };

    function onAdd() {
        if (!items) {
            
        } else {
            setallItems((oldItems) => {
                const allitemmsss = {id: new Date().getTime().toString(), name: items}
                return [...oldItems, allitemmsss];
            });
            setitens('');
        }
    };

    function deleteItem(id){
        setallItems((oldItems) => {
            return oldItems.filter((arrElement, inde) => {
                return index !== id;
            })
        })
    }

    function removeAll(){
        setallItems([]);
    }

    return (
        <>
            <div className="main">
                <div className="content">
                    <h1>HELLO!! WELCOME</h1>
                    <p>This is a basic ToDo List App</p>
                    <button>GET STARTED</button>
                </div>
                <div className="center">
                    <br />
                    <h1>ToDo List</h1>
                    <br />
                    <input type="text"
                        placeholder="Add an Item"
                        onChange={eventHandler}
                        value = {items} />
                    <button title='add item ' onClick={onAdd}>+</button>
                    <ol>
                        {allItems.map((allItemsValue) => {
                            return(
                                <ToDoLiatsItems
                                text = {allItemsValue.name}
                                id = {allitemmsss.id}
                                key = {index}
                                onselect = {deleteItem}
                                />
                            );
                        })}
                    </ol>
                    <button title='remove all items' className='remove' onClick={removeAll}>REMOVE ALL</button>
                </div>
            </div>
        </>
    );
};

export default ToDo_list;
